    AudioStream audio;
    JButton pressed_button;
    String pressed="";
    String data="ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
    String s="";
    ImageIcon i;
    public keyboard_letters() {
        initComponents();
        setSize(1025,625);
        setSize(1050,650);
        Toolkit t=Toolkit.getDefaultToolkit();
        Dimension d=t.getScreenSize();
        setLocation(((int)(d.getWidth())-1025)/2,((int)(d.getHeight())-625)/2);
        int select=tell();
        s=data.substring(select,select+1);
        jLabel1.setText(s);
        jButton28.setEnabled(false);
        i=new ImageIcon(s+" (180 x 128).png");
        jLabel3.setIcon(i);
    }
    int tell()
    {
       int result=(int)(35*(Math.random()));
       return result;
    }
    void check()
    {
        if(s.equals(pressed))
        {
            jLabel2.setVisible(true);
            ImageIcon i=new ImageIcon("correct.png");
            jLabel2.setIcon(i);
            jButton28.setEnabled(true);
        }
        else
        {
            jLabel2.setVisible(true);
            ImageIcon i=new ImageIcon("wrong.png");
            jLabel2.setIcon(i);
            jButton28.setEnabled(false);
        }
    }

