    void play_sound()
    {
        try
        {
        FileInputStream fin=new FileInputStream(s+"_001.au");
        audio=new AudioStream(fin);
        AudioPlayer.player.start(audio);
        }
        catch(Exception e)
        {
            
        }
    }
